import React from "react";
import { useEffect, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import bookingservice from "../service/bookingservice";
import AddTicket from "../com/AddTicket";
const Addbooking = () => {
  const [train_no, setTrainNo] = useState("");
  const [train_name, setTrainName] = useState("");

  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [age, setAge] = useState("");

  const [start_from, setStartFrom] = useState("");
  const [destination, setDestination] = useState("");
  const [emailId, setEmailId] = useState("");

  const navigate = useNavigate();
  const { id } = useParams();

  const saveBooking = (e) => {
    e.preventDefault();

    const booking = {
      train_no,
      train_name,
      fname,
      lname,
      age,
      start_from,
      destination,
      emailId,
      id,
    };

    if (id) {
      //update
      bookingservice
        .update(booking)
        .then((response) => {
          console.log("booking data updated successfully", response.data);
          navigate("/Booking");
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    } else {
      // create
      bookingservice
        .create(booking)
        .then((response) => {
          console.log("booking added successfully", response.data);
          navigate("/Booking");
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    }
  };

  useEffect(() => {
    if (id) {
      bookingservice
        .get(id)
        .then((bookings) => {
          setTrainNo(bookings.data.train_no);
          setTrainName(bookings.data.train_name);
          setFname(bookings.data.fname);
          setLname(bookings.data.lname);
          setAge(bookings.data.age);
          setStartFrom(bookings.data.start_from);
          setDestination(bookings.data.destination);
          setEmailId(bookings.data.emailId);
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    }
  }, []);

  return (
    <>
      <section className="showcase login">
        <div className="showcase-overlay">
          <form className="form-control">
            <input
              type="number"
              placeholder="TrainNo"
              value={train_no}
              onChange={(e) => setTrainNo(e.target.value)}
            />
            <input
              type="text"
              placeholder="Enter TrainName"
              value={train_name}
              onChange={(e) => setTrainName(e.target.value)}
            />
            <input
              type="text"
              placeholder="Enter Firstname"
              value={fname}
              onChange={(e) => setFname(e.target.value)}
            />
            <input
              type="text"
              placeholder="Enter Lastname"
              value={lname}
              onChange={(e) => setLname(e.target.value)}
            />
            <input
              type="number"
              placeholder="Age"
              value={age}
              onChange={(e) => setAge(e.target.value)}
            />
            <input
              type="text"
              placeholder="Start From"
              value={start_from}
              onChange={(e) => setStartFrom(e.target.value)}
            />
            <input
              type="text"
              placeholder="Destination"
              value={destination}
              onChange={(e) => setDestination(e.target.value)}
            />
            <input
              type="text"
              placeholder="Your EmailId"
              value={emailId}
              onChange={(e) => setEmailId(e.target.value)}
            />

            <Link
              style={{
                marginLeft: "48rem",
                background: "beige",
                width: "20%",
                marginLeft: "0.5rem",
                marginTop: "0.5rem",
                height: "20%",
              }}
              className="btn btn-sucess"
              to="/AddTicket"
              onClick={() => {
                saveBooking();
              }}
            >
              Confirm Booking
            </Link>
          </form>
        </div>
      </section>
    </>
  );
};

export default Addbooking;
